import React from "react";
import { Link } from "react-router-dom";
import Card from 'react-bootstrap/Card';

export const recommended = [
    {
        id: 1,
        cover: "../images/u1.jpeg",
        name: "Ishque",
        time: "2hr : 38mins",
    },
    {
        id: 2,
        cover: "../images/u2.jpg",
        name: "Supreme",
        time: "2hr : 38mins",
    },
    {
        id: 3,
        cover: "../images/u3.jpg",
        name: "Love story",
        time: "2hr : 38mins",
    },
    {
        id: 4,
        cover: "../images/u4.jpg",
        name: "Uppena",
        time: "2hr : 38mins",
    },
    {
        id: 3,
        cover: "../images/u5.jpg",
        name: "Life is Beautiful",
        time: "2hr : 38mins",
    },
]

export const Upcoming = () => {
    return (
        <>
            <div className="upcoming">
                <h1 style={{ color: 'white' }}>upcoming movies</h1>
                <Link to='/' style={{ color: 'red' }}> Viewall</Link>
            </div>
            <div className="box">
                <div className="images">
                    {recommended.map((image) => (
                        <>
                            <Card className="bg-dark text-white">
                                <Card.Img src={image.cover} alt="" />
                                <Card.ImgOverlay>
                                    <Card.Title>{image.name}</Card.Title>
                                    <Card.Text>
                                        {image.time}
                                    </Card.Text>
                                    <button className='primary-btn'>
                                        <i className='fa fa-play'></i> PLAY NOW
                                    </button>
                                </Card.ImgOverlay>
                            </Card>
                            {/* <img src={image.cover} alt="" />
                            <h3 style={{ color: "white" }}>{image.name}</h3>
                            <span style={{ color: "white" }}>{image.time}</span>
                            <button className='primary-btn'>
                                <i className='fa fa-play'></i> PLAY NOW
                            </button> */}
                        </>

                    ))}
                </div>
                <div className='text'>
                    {/* {recommended.map((image) =>(
               <h3>{image.name}</h3>,
               <span>{image.time}</span>,
               <button className='primary-btn'>
                 <i className='fa fa-play'></i> PLAY NOW
               </button>
            ))} */}

                </div>
            </div>
        </>
    )
}

export default Upcoming;
