import React, { useState } from "react";
import { Link } from "react-router-dom";

export const recommended = [
    {
      id: 1,
      cover: "../images/img11.jpeg",
      name: "Ishque",
      time: "2hr : 38mins",
    },
    {
      id: 2,
      cover: "../images/img12.jpeg",
      name: "Supreme",
      time: "2hr : 38mins",
    },
    {
      id: 3,
      cover: "../images/img13.jpg",
      name: "Love story",
      time: "2hr : 38mins",
    },
    {
      id: 4,
      cover: "../images/img14.jpg",
      name: "Uppena",
      time: "2hr : 38mins",
    },
    {
      id: 3,
      cover: "../images/img15.jpg",
      name: "Life is Beautiful",
      time: "2hr : 38mins",
    },
  ]

export const Upcoming = ({item: {cover}}) => {
    const [items, setitems] = useState(recommended)
    return (
        <>
        <section className='recommended'>
        <recommended items={items} />
      </section>
        <div className="upcoming">
            <h1 style={{ color: 'white'}}>upcoming movies</h1>
            <Link to='/' style={{ color: 'red'}}> Viewall</Link>
        </div>
        <div className="images">
        {items.map((item) => {
                return (
                  <>
                    <img src={cover} alt="" />
                  </>
                )
              })}
         
        </div>
        </>
    )
}

export default Upcoming;
